<?php
session_start();

// Inisialisasi token CSRF
if (empty($_SESSION['csrf_token'])) {
    $_SESSION['csrf_token'] = bin2hex(random_bytes(32));
}
$csrf_token = $_SESSION['csrf_token'];

// Fungsi untuk menghasilkan password
function generatePasswords($keyword, $count, $strength) {
    $passwords = [];
    $chars = '0123456789!@#$%^&*()_+-=ABCDEFGHIJKLMOPQRSTUVWXYZ';
    $length = 8;

    switch ($strength) {
        case 'medium':
            $length = 16;
            break;
        case 'strong':
            $length = 32;
            break;
        default:
            $length = 8;
    }

    for ($i = 0; $i < $count; $i++) {
        $password = $keyword;
        $len = rand($length - strlen($keyword), $length - strlen($keyword));
        for ($j = 0; $j < $len; $j++) {
            $password .= $chars[rand(0, strlen($chars) - 1)];
        }
        $passwords[] = str_shuffle($password);
    }
    return $passwords;
}

// Inisialisasi variabel untuk hasil
$results = [];
$error = '';
$keyword = '';
$strength = 'weak';

if ($_SERVER['REQUEST_METHOD'] == 'POST' && hash_equals($_SESSION['csrf_token'], $_POST['csrf_token'])) {
    $keyword = isset($_POST['keyword']) ? htmlspecialchars($_POST['keyword'], ENT_QUOTES, 'UTF-8') : '';
    $strength = isset($_POST['strength']) ? $_POST['strength'] : 'weak';

    if (strlen($keyword) < 4) {
        $error = 'Keyword harus minimal 4 karakter.';
    } elseif (strlen($keyword) > 12) {
        $error = 'Keyword tidak boleh lebih dari 12 karakter.';
    } else {
        $results = generatePasswords($keyword, 10, $strength);
    }
}

// Fungsi untuk mengexport password ke file txt
if (isset($_POST['export'])) {
    $passwordsToExport = $_POST['passwords'];
    $timestamp = date("d/m/y H:i:s");
    $filename = "R4hasia_$timestamp.txt";

    header('Content-Type: text/plain');
    header('Content-Disposition: attachment; filename="' . $filename . '"');

    echo "Generated Passwords (Downloaded on $timestamp):\n\n";
    foreach ($passwordsToExport as $password) {
        echo $password . "\n";
    }
    exit;
}
